// TestInboundHrmpMessage tests the scale encoding of an InboundHrmpMessage
func TestInboundHrmpMessage(t *testing.T) {
	t.Parallel()

	result := "0x28e803000000d007000000d207000000d407000000db07000000ee07000000f007000000f207000000f307000000fe07000000"
	resultBytes, err := common.HexToBytes(result)
	require.NoError(t, err)

	var msg []InboundHrmpMessage
	err = scale.Unmarshal(resultBytes, &msg)
	require.NoError(t, err)

	expected := []InboundHrmpMessage{
		{
			SentAt: 1000,
			Data:   []byte{},
		},
		{
			SentAt: 2000,
			Data:   []byte{},
		},
		{
			SentAt: 2002,
			Data:   []byte{},
		},
		{
			SentAt: 2004,
			Data:   []byte{},
		},
		{
			SentAt: 2011,
			Data:   []byte{},
		},
		{
			SentAt: 2030,
			Data:   []byte{},
		},
		{
			SentAt: 2032,
			Data:   []byte{},
		},
		{
			SentAt: 2034,
			Data:   []byte{},
		},
		{
			SentAt: 2035,
			Data:   []byte{},
		},
		{
			SentAt: 2046,
			Data:   []byte{},
		},
	}
	require.Equal(t, expected, msg)

	encoded, err := scale.Marshal(msg)
	require.NoError(t, err)
	require.Equal(t, result, common.BytesToHex(encoded))
}
