package grandpa

import (
	"bytes"
	"testing"

	"github.com/ChainSafe/gossamer/lib/common"
	"github.com/ChainSafe/gossamer/lib/crypto/ed25519"
	"github.com/ChainSafe/gossamer/lib/keystore"
	"github.com/ChainSafe/gossamer/lib/scale"

	"github.com/stretchr/testify/require"
)

func TestPubkeyToVoter(t *testing.T) {
	kr, err := keystore.NewEd25519Keyring()
	require.NoError(t, err)

	state := NewState(voters, 0, 0)
	voter, err := state.pubkeyToVoter(kr.Alice().Public().(*ed25519.PublicKey))
	require.NoError(t, err)
	require.Equal(t, voters[0], voter)
}

func TestSignedVoteEncoding(t *testing.T) {
	just := &SignedVote{
		Vote:        testVote,
		Signature:   testSignature,
		AuthorityID: testAuthorityID,
	}

	enc, err := just.Encode()
	require.NoError(t, err)

	rw := &bytes.Buffer{}
	rw.Write(enc)
	dec := new(SignedVote)
	_, err = dec.Decode(rw)
	require.NoError(t, err)
	require.Equal(t, just, dec)
}

func TestSignedVoteArrayEncoding(t *testing.T) {
	just := []*SignedVote{
		{
			Vote:        testVote,
			Signature:   testSignature,
			AuthorityID: testAuthorityID,
		},
	}

	enc, err := scale.Encode(just)
	require.NoError(t, err)

	dec, err := scale.Decode(enc, make([]*SignedVote, 1))
	require.NoError(t, err)
	require.Equal(t, just, dec.([]*SignedVote))
}

func TestJustification(t *testing.T) {
	just := &SignedVote{
		Vote:        testVote,
		Signature:   testSignature,
		AuthorityID: testAuthorityID,
	}

	fj := &Justification{
		Round: 99,
		Commit: &Commit{
			Precommits: []*SignedVote{just},
		},
	}
	enc, err := fj.Encode()
	require.NoError(t, err)

	rw := &bytes.Buffer{}
	rw.Write(enc)
	dec := &Justification{}
	err = dec.Decode(rw)
	require.NoError(t, err)
	require.Equal(t, fj, dec)
}

func TestJustification_Decode(t *testing.T) {
	// data received from network
	data := common.MustHexToBytes("")
	fj := new(Justification)
	rw := &bytes.Buffer{}
	rw.Write(data)

	err := fj.Decode(rw)
	require.NoError(t, err)
	require.Equal(t, uint64(6971), fj.Round)
	require.Equal(t, uint32(4635975), fj.Commit.Number)
	require.Equal(t, common.MustHexToHash("0x2a82146e771968df054c8036040dea584339df52d8cbac6970d4c22ed59f7022"), fj.Commit.Hash)
	require.Equal(t, 199, len(fj.Commit.Precommits))
}
