import { map } from 'rxjs';
import { memo } from "../util/index.js"; // re-export these - since these needs to be resolvable from api-derive, i.e. without this
// we would emit code with ../<somewhere>/src embedded in the *.d.ts files

export function unwrapBlockNumber(fn) {
  return (instanceId, api) => memo(instanceId, () => fn(api).pipe(map(r => r.number.unwrap())));
}